function pcDraw(evt) {
  console.log("");
  console.log("[pcDraw] : [start]");
  console.log("");
  switch (evt.type) {
    case "mousedown":
      {
        BodyScrollDisAble(); //body 스크롤 정지
        drawble = true;
        ctx.beginPath();
        ctx.moveTo(getPcPosition(evt).X, getPcPosition(evt).Y);
      }
      break;
    case "mousemove":
      {
        if (drawble) {
          ctx.lineTo(getPcPosition(evt).X, getPcPosition(evt).Y);
          ctx.stroke();
        }
      }
      break;
    case "mouseup":
    case "mouseout":
      {
        BodyScrollDisAble(); //body 스크롤 허용
        drawble = false;
        ctx.closePath();
      }
      break;
  }
}
