import { StreamBarcodeReader } from "vue-barcode-reader";

<template>
    <div>
        <div class="wrap">

            <!-- ======== 카테고리 헤더 ======== -->
            <div class="header">
                <div class="main_logo" v-on:click="goHome"></div>
                <div class="header_title">
                    <div class="header_title_down">택배원 인증</div>    
                </div>
                <div class="back"  v-on:click="goBack"></div>    
            </div>

            <!-- ======== 내용 ======== -->
            <div class="checkmanager_container">
                <div class="checkmanager_area" v-on:click="goMemberNoScan">
                </div>
            </div><!--container끝-->

            <div class="checkmanager_ment" v-on:click="goMemberNoScan">
            </div>
        </div><!--wrap 끝-->

    </div>
</template>
<script>
import '@/assets/css/input/checkmanager.css';
import axios from 'axios';

//import dayjs from "dayjs"
//const nowDay_json = "@/assets/json/" + dayjs().format('YYYY-MM-DD') + ".json";
//import json_day_log from nowDay_json;

export default {
    name: "CheckManager",
    data() {
        return {
            log_data :{postNo:'1111101',boxNo:'01',menuName:'사원증스캔',actionData:'사원증 인증'},
            request2_data :{userId:'deli001',
                            userName:'',
                            company:'',
                            status:'',
                            userType:'',
                            createDate:'',
                            updateDate:'',
                            deleted:''
                            },
        };
    },
    methods: {
        goBack(){
            this.$router.go(-1);
        },
        goHome(){
            this.$router.push('/');
        },
        goMemberNoScan(){
            // 로그처리
            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]사원증스캔';
            this.log_data.actionData = '사원 인증';
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);
                console.log("성공", res.errno);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            // 사원증 인증확인
            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]사원증스캔';
            this.log_data.actionData = '사원 인증';
            axios.get("http://localhost:8910/admin/member/detail", this.request2_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);
                console.log("성공", res.data.errno);

                if (res.data.errno == -1) {
                    console.log(res.data.errmsg, this.request2_data.userId);
                    this.$router.push('/reginoscan');
                } else {                
                    this.$router.push('/reginoscan');
                }

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

        },
    },
    created() {
    },
    mounted() {
    }

};
</script>
