import { StreamBarcodeReader } from "vue-barcode-reader";

<template>
    <div>
        <div class="wrap">

            <!-- ======== 카테고리 헤더 ======== -->
            <div class="header">
                <div class="main_logo" v-on:click="goHome"></div>
                <div class="header_title">
                    <div class="header_title_down">주민증 앞면</div>    
                </div>
                <div class="back"  v-on:click="goBack"></div>    
            </div>

            <!-- ======== 내용 ======== -->
            <div class="juminfront_container">
                <div class="juminfront_area">
                    <div class="juminfront_idcard" v-on:click="goJuminBack">
                    </div>
                </div>
                <div class="juminfront_info">
                    <div class="juminfront_name" v-on:click="goName">
                    </div>
                    <div class="juminfront_no" v-on:click="goNo">
                    </div>
                    <div class="juminfront_addr" v-on:click="goAddr">
                    </div>
                    <div class="juminfront_addrdetail" v-on:click="goAddrDetail">
                    </div>
                    <div class="column_info">
                        <div class="ss_label"  id="ss_name_label" >이름</div>
                        <div class="ss_input"  id="ss_name" contenteditable="true" onClick="kbd_click('ss_name')"></div>
                        <div class="ss_img" onClick="mic_click('ss_name')"></div>
                    </div>

                    <div class="column_info">
                        <div class="ss_label"  id="ss_phoneno_label">주민번호</div>
                        <div class="ss_input"  id="ss_phoneno" contenteditable="true" onClick="kbd_click('ss_phoneno')">02-9901-0999</div>
                    </div>

                    <div class="column_info">
                        <div class="ss_label"  id="ss_addr_label">* 주소</div>
                        <div class="ss_input_short"  id="ss_addr" contenteditable="true" onClick="kbd_click('ss_addr')"></div>
                        <div class="ss_img" onClick="mic_click('ss_addr')"></div>
                        <div class="ss_button"  onClick="goMenu('findaddr.html?cate=easy_menu01_sender');" >검색</div>
                    </div>

                    <div class="column_info">
                        <div class="ss_label"  id="ss_addr2_label">상세주소</div>
                        <div class="ss_input"  id="ss_addr2" contenteditable="true" onClick="kbd_click('ss_addr2')">화수목빌딩 6층</div>
                    </div>
                </div>
                <div class="juminfront_ment">
                </div>
            </div><!--container끝-->
            <div class="juminfront_tail">
            </div>

        </div><!--wrap 끝-->

    </div>
</template>
<script>
import '@/assets/css/receipt/juminfront.css';
import { socket } from "@/socket";

import axios from 'axios';

//import dayjs from "dayjs"
//const nowDay_json = "@/assets/json/" + dayjs().format('YYYY-MM-DD') + ".json";
//import json_day_log from nowDay_json;

export default {
    name: "JuminFront",
    data() {
        return {
            log_data :{},
        };
    },
    methods: {
        goBack(){
            this.$router.go(-1);
        },
        goHome(){
            this.$router.push('/');
        },
        goJuminBack(){
            this.$router.push('/juminback');
        },
        onSubmit() {
            this.isLoading = true;

            socket.timeout(5000).emit("바보", this.value, () => {
            this.isLoading = false;

            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]주민등록증';
            this.log_data.actionData = '전면스캔';
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

      });
    },

    },
    created() {
    },
    mounted() {
    }

};
</script>
