                console.log("성공", res.errno);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            // 사원증 인증확인
            this.member_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]사원증스캔';
            this.log_data.actionData = '사원 인증 확인';
            axios.get("http://localhost:8910/admin/member/detail", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);
                console.log("성공", res.data.errno);

                if (res.data.errno == -1) {
                    console.log(res.data.errmsg, this.request2_data.userId);
                    this.$router.push('/reginoscan');
                } else {                
                    this.$router.push('/reginoscan');
                }

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

        },
    },
    created() {
    },
