import { StreamBarcodeReader } from "vue-barcode-reader";

<template>
    <div>
        <div class="wrap">

            <!-- ======== 카테고리 헤더 ======== -->
            <div class="header">
                <div class="main_logo" v-on:click="goHome"></div>
                <div class="header_title">
                    <div class="header_title_down">받는 사람</div>    
                </div>
                <div class="back"  v-on:click="goBack"></div>    
            </div>

            <!-- ======== 내용 ======== -->
            <div class="receiver_container">
                <div class="column_info">
                    <div class="rr_label"  id="rr_handphoneno_label">* 휴대폰번호</div>
            <div class="rr_input"  id="rr_handphoneno" contenteditable="true" onClick="kbd_click('rr_handphoneno')">010-5933-9878</div>
                </div>

        <div class="column_info">
            <div class="rr_label"  id="rr_name_label" >* 이름</div>
            <div class="rr_input"  id="rr_name" contenteditable="true" onClick="kbd_click('rr_name')">윤혜린</div>
            <div class="rr_img" onClick="mic_click('rr_name')"></div>
        </div>

        <div class="column_info">
            <div class="rr_label"  id="rr_phoneno_label">전화번호</div>
            <div class="rr_input"  id="rr_phoneno" contenteditable="true" onClick="kbd_click('rr_phoneno')">02-8911-3868</div>
        </div>
        <div class="column_info" style="display:none;">
            <div class="rr_label"  id="rr_zipcode_label">우편번호</div>
            <div class="rr_input"  id="rr_zipcode" contenteditable="true" onClick="kbd_click('rr_zipcode')">04098</div>
            <div class="rr_img" onClick="mic_click('rr_zipcode')"></div>
        </div>
        <div class="column_info">
            <div class="rr_label"  id="rr_addr_label">* 주소</div>
            <div class="rr_input_short"  id="rr_addr" contenteditable="true" onClick="kbd_click('rr_addr')" ></div>
            <div class="rr_img" onClick="mic_click('rr_addr')"></div>
            <div class="rr_button"  onClick="goMenu('findaddr.html?cate=easy_menu01_receiver');" >검색</div>
        </div>

        <div class="column_info">
            <div class="rr_label"  id="rr_addr2_label">상세주소</div>
            <div class="rr_input"  id="rr_addr2" contenteditable="true" onClick="kbd_click('rr_addr2')">화수목빌딩 6층</div>
        </div>

        <div type="hidden" class="myitem" id="myitem"></div>  
        <input  type="hidden" class="myinput" id="myinput" name="myinput" />

        <div class="phonepad_info">
            <div class="pad_num">
                <div class="num1"  onclick="addNum_handphoneno('1')"></div>
                <div class="num2"  onclick="addNum_handphoneno('2')"></div>
                <div class="num3"  onclick="addNum_handphoneno('3')"></div>
            </div>
            <div class="pad_num">  
                <div class="num4"  onclick="addNum_handphoneno('4')"></div>
                <div class="num5"  onclick="addNum_handphoneno('5')"></div>
                <div class="num6"  onclick="addNum_handphoneno('6')"></div>
            </div>
            <div class="pad_num">  
                <div class="num7"  onclick="addNum_handphoneno('7')"></div>
                <div class="num8"  onclick="addNum_handphoneno('8')"></div>
                <div class="num9"  onclick="addNum_handphoneno('9')"></div>
            </div>
            <div class="pad_num">  
                <div class="num0"  onclick="addNum_handphoneno('0')"></div>
                <div class="num_back"  onclick="addNum_handphoneno('11')"></div>
                <div class="numconfirm"  onclick="checkHandPhoneno()"></div>
            </div>
        </div>

        <div class="keyboardzone" id="keyboardzone"></div>
        <div class="voicezone" id="voicezone" ></div>

            </div><!--container끝-->

        </div><!--wrap 끝-->

    </div>
</template>
