import axios from 'axios'
//import { Message, MessageBox } from 'element-ui'
import store from '@/store'
import { getToken } from '@/utils/auth'

const service = axios.create({
  baseURL: process.env.BASE_API, 
  timeout: 5000 // request timeout
})

// request interceptor
service.interceptors.request.use(
  config => {
    // Do something before request is sent
    if (store.getters.token) {
      config.headers['X-KoreaPost-Token'] = getToken()
    }
    return config
  },
  error => {
    // Do something with request error
    console.log(error) // for debug
    Promise.reject(error)
  }
)

// response interceptor
service.interceptors.response.use(
  response => {
    const res = response.data

    if (res.errno === 501) {
      MessageBox.alert('다시 로그인 해주세요.', '알림', {
        confirmButtonText: '확인',
        type: 'error'
      }).then(() => {
        store.dispatch('FedLogOut').then(() => {
          location.reload()
        })
      })
      return Promise.reject('error')
    } else if (res.errno === 502) {
      MessageBox.alert('관리자에게 문의 바랍니다.', '알림', {
        confirmButtonText: '확인',
        type: 'error'
      })
      return Promise.reject('error')
    } else if (res.errno === 503) {
      MessageBox.alert('현재 지원되지 않습니다.', '알림', {
        confirmButtonText: '확인',
        type: 'error'
      })
      return Promise.reject('error')
    } else if (res.errno === 504) {
      MessageBox.alert('페이지를 새로고침 해 주세요.', '알림', {
        confirmButtonText: '확인',
        type: 'error'
      })
      return Promise.reject('error')
    } else if (res.errno === 505) {
      MessageBox.alert('업데이트에 실패 했습니다.', '알림', {
        confirmButtonText: '확인',
        type: 'error'
      })
      return Promise.reject('error')
    } else if (res.errno === 506) {
      MessageBox.alert('관리자 권한이 없습니다.', '알림', {
        confirmButtonText: '확인',
        type: 'error'
      })
      return Promise.reject('error')
    } else if (res.errno !== 0) {
      return Promise.reject(response)
    } else {
      return response
    }
  }, error => {
    console.log('err' + error)// for debug
    Message({
      message: '세션시간이 종료 되었습니다. 다시 로그인 해주세요.',
      type: 'error',
      duration: 5 * 1000
    })
    return Promise.reject(error)
  })

export default service
