    </div>
</template>
<script>
import '@/assets/css/input/reginoscan.css';
import axios from 'axios';
import { QrcodeStream } from 'vue-qrcode-reader'

//import dayjs from "dayjs"
//const nowDay_json = "@/assets/json/" + dayjs().format('YYYY-MM-DD') + ".json";
//import json_day_log from nowDay_json;

export default {
    name: "RegiNoScan",
    data() {
        return {
            log_data :{},
            regi_data :{},
        };
    },
    components : {
        QrcodeStream,
    },
    methods: {
        goBack(){
            this.$router.go(-1);
        },
        goHome(){
            this.$router.push('/');
        },
        onDecode (result) {
            this.qrCodeData = result;
        },
        goSelectBox(){
            // 로그처리
            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]등기번호스캔';
            this.log_data.actionData = '등기번호 인증';
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            // 등기번호 인증
            this.regi_data.postNo = '1111101';
            axios.get("http://localhost:8910/admin/dispenser/checkregino", this.regi_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);
                console.log("성공", res.data.errno);

                if (res.data.errno == -1) {
                    console.log(res.data.errmsg, this.request2_data.userId);
                    this.$router.push('/selectbox');
                } else {                
                    this.$router.push('/selectbox');
                }

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            this.$router.push('/selectbox');
        }     
    },
    created() {
    },
    mounted() {
    }

};
</script>
