            </div>
        </div><!--wrap 끝-->

    </div>
</template>
<script>
import '@/assets/css/receipt/drivingcard.css';

import axios from 'axios';
import { socket } from "@/socket";

//import dayjs from "dayjs"
//const nowDay_json = "@/assets/json/" + dayjs().format('YYYY-MM-DD') + ".json";
//import json_day_log from nowDay_json;

export default {
    name: "DrivingCard",
    data() {
        return {
            log_data :{},
            recvMsg: [],
            recv_name:'',
            recv_jumin:'',
            recv_addr:'',
            recv_addr2:'',
        };
    },
    methods: {
        goBack(){
            this.$router.go(-1);
        },
        goHome(){
            this.$router.push('/');
        },
        goScan(){
            socket.connect();

            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]운전면허증';
            this.log_data.actionData = '인식기Socket연결';
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]운전면허증';
            this.log_data.actionData = '인식요청';
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            socket.timeout(5000).emit("ID3", "", () => {
            });

            ///this.$router.push('/');
        },
        goReceiptSign(){
            var name = document.querySelector('#drivingcard_name').innerText;
            var jumin = document.querySelector('#drivingcard_jumin').innerText;
            var addr = document.querySelector('#drivingcard_addr').innerText;
            var addr2 = document.querySelector('#drivingcard_addr2').innerText;

            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]운전면허증';
            this.log_data.actionData = '전면스캔:'+name+'/'+jumin+'/'+addr+'/'+addr2;
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });
            this.$router.push('/receiptsign');
        },
        // 수취인정보로 등기정보조회
        postRegiNo() {
            axios.get("https://postnet.co.kr/retrieveRegiMailRecPrsnInfo.jsp", this.request_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);
                // regiNo : 등기번호 - 13

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });
        }

    },
    created() {
    },
    mounted() {
    }

};
</script>
