import net from 'net';

export default {
  data() {
    return {
      ip: '127.0.0.1',
      port: '9001',
      message: 'koreapost',
      socket: null
    }
  },
  mounted() {
//    const socket = new net.Socket()
//    this.socket = socket
//    socket.on('data', (data) => console.log(data.toString()))
  },
  methods: {
    connect() {
      this.socket.connect(this.port, this.ip)
    },
    sendMessage() {
      this.socket.write(this.message)
    }
  }
}
