            </div>
        </div><!--wrap 끝-->

    </div>
</template>
<script>
import '@/assets/css/receipt/juminfront.css';

import axios from 'axios';
import { socket } from "@/socket";

//import dayjs from "dayjs"
//const nowDay_json = "@/assets/json/" + dayjs().format('YYYY-MM-DD') + ".json";
//import json_day_log from nowDay_json;

export default {
    name: "JuminFront",
    data() {
        return {
            log_data :{},
            recvMsg: [],
            recv_name:'',
            recv_jumin:'',
            recv_addr:'',
            recv_addr2:'',
          };
    },
    methods: {
        goBack(){
            this.$router.go(-1);
        },
        goHome(){
            this.$router.push('/');
        },
        goFrontScan(){
            socket.connect();

            socket.on('OK1', (messages) => {
                this.recvMsg = messages
                console.log("OK1 수신:", messages);

                var r_data = messages.split('|');
                this.recv_name = r_data[0];
                this.recv_jumin = r_data[1];
                this.recv_addr = r_data[2];
                this.recv_addr2 = r_data[3];
            });
            socket.on('OK2', (messages) => {
                this.recvMsg = messages
                console.log("OK2 수신:", messages);

                this.recv_addr = messages;
            });
            socket.on('OK3', (messages) => {
                this.recvMsg = messages
                console.log("OK3 수신:", messages);

                var r_data = messages.split('|');
                this.recv_name = r_data[0];
                this.recv_jumin = r_data[1];
                this.recv_addr = r_data[2];
                this.recv_addr2 = r_data[3];
            });
            socket.on('ING', (messages) => {
                this.recvMsg = messages
                console.log("ING 수신:", messages);
            });
            socket.on('ERR', (messages) => {
                this.recvMsg = messages
                console.log("ERR31 수신:", messages);
            });
            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]주민등록증';
            this.log_data.actionData = '인식기Socket연결';
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]주민등록증';
            this.log_data.actionData = '앞면인식요청';
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            socket.timeout(5000).emit("ID1", "", () => {
            });



            ///this.$router.push('/');
        },
        goJuminBack(){
            var name = document.querySelector('#juminfront_name').innerText;
            var jumin = document.querySelector('#juminfront_jumin').innerText;
            var addr = document.querySelector('#juminfront_addr').innerText;
            var addr2 = document.querySelector('#juminfront_addr2').innerText;

            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]주민등록증';
            this.log_data.actionData = '전면스캔확인:'+name+'/'+jumin+'/'+addr+'/'+addr2;
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

            this.$router.push('/juminback');
        },
        onSubmit() {

            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]주민등록증';
            this.log_data.actionData = '전면스캔';
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });

    },

    },
    created() {
    },
    mounted() {
            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]주민등록증';
            this.log_data.actionData = '전면스캔';
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });
    }

};
</script>
