import { StreamBarcodeReader } from "vue-barcode-reader";

<template>
    <div>
        <div class="wrap">

            <!-- ======== 카테고리 헤더 ======== -->
            <div class="header">
                <div class="main_logo" v-on:click="goHome"></div>
                <div class="header_title">
                    <div class="header_title_down">운전면허증</div>    
                </div>
                <div class="back"  v-on:click="goBack"></div>    
            </div>

            <!-- ======== 내용 ======== -->
            <div class="drivingcard_container">
                <div class="drivingcard_area">
                    <div class="drivingcard_drivingcard" v-on:click="goReceiptSign">
                    </div>
                </div>
                <div class="drivingcard_info">
                    <div class="column_info">
                        <div class="ss_label"  id="ss_name_label" >이름</div>
                        <div class="ss_input"  id="ss_name" contenteditable="true" v-on:onClick="kbd_click('ss_name')"></div>
                    </div>

                    <div class="column_info">
                        <div class="ss_label"  id="ss_phoneno_label">주민번호</div>
                        <div class="ss_input"  id="ss_phoneno" contenteditable="true" v-on:onClick="kbd_click('ss_phoneno')"></div>
                    </div>

                    <div class="column_info">
                        <div class="ss_label"  id="ss_addr_label">주소</div>
                        <div class="ss_input"  id="ss_addr"></div>
<!--
                        <div class="ss_input_short"  id="ss_addr" contenteditable="true" v-on:onClick="kbd_click('ss_addr')"></div>
                        <div class="ss_button"  onClick="goMenu('findaddr.html?cate=easy_menu01_sender');" >검색</div>
                        -->
                    </div>

                    <div class="column_info">
                        <div class="ss_label"  id="ss_addr2_label">상세주소</div>
                        <div class="ss_input"  id="ss_addr2"></div>
                    </div>
                </div>


                <div class="drivingcard_ment">
                </div>
            </div><!--container끝-->

            <div class="drivingcard_tail">
                <div class="drivingcard_rescan" v-on:click="goScan">
                </div>
                <div class="drivingcard_done" v-on:click="goReceiptSign">
                </div>
            </div>
        </div><!--wrap 끝-->

    </div>
</template>
<script>
import '@/assets/css/receipt/drivingcard.css';

import axios from 'axios';

//import dayjs from "dayjs"
//const nowDay_json = "@/assets/json/" + dayjs().format('YYYY-MM-DD') + ".json";
//import json_day_log from nowDay_json;

export default {
    name: "DrivingCard",
    data() {
        return {
            log_data :{},
        };
    },
    methods: {
        goBack(){
            this.$router.go(-1);
        },
        goHome(){
            this.$router.push('/');
        },
        goReceiptSign(){
            this.log_data.postNo = '1111101';
            this.log_data.boxNo = '';
            this.log_data.userId = '';
            this.log_data.menuName = '[메뉴]운전면허증';
            this.log_data.actionData = '전면스캔';
            // 로그처리
            axios.post("http://localhost:8910/admin/dispenserlog/create", this.log_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });
            this.$router.push('/confirmdrivingcard');
        },
        // 수취인정보로 등기정보조회
        postRegiNo() {
            axios.get("https://postnet.co.kr/retrieveRegiMailRecPrsnInfo.jsp", this.request_data, {
                header: {
                    "Context-Type": "multipart/form-data",
                },
            })
            .then((res) => { // 성공했을 경우
                console.log("성공", res);
                // regiNo : 등기번호 - 13

            })
            .catch((res) => { // 실패했을 경우
                console.error("실패 ", res);
            });
        }

    },
    created() {
    },
    mounted() {
    }

};
</script>
